import {Component, OnDestroy, OnInit} from '@angular/core';
import {Subscription} from "rxjs";
import {AuthService} from "../../auth/auth.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit, OnDestroy{
userIsAuthenticated: boolean = false;
private authListenerSub: Subscription | undefined;

constructor(private authService: AuthService) {
}

  ngOnDestroy(): void {
  if(this.authListenerSub){
    this.authListenerSub.unsubscribe();
  }

  }

  ngOnInit(): void {
    this.authService.getAuthStatusListener().subscribe((isAuthenticated: boolean)=>{
      this.userIsAuthenticated = isAuthenticated;
    });

  }


}
<mat-toolbar color="primary" >
  <span routerLink="/"><a>My App</a></span>
  <span class="spacer"></span>
  <ul>
    <li *ngIf="userIsAuthenticated">
      <a mat-button routerLink="/create" routerLinkActive="active">New Post</a>
    </li>
    <li *ngIf="!userIsAuthenticated">
      <a mat-button routerLink="/login" routerLinkActive="active">Login</a>
    </li>
    <li *ngIf="!userIsAuthenticated">
      <a mat-button routerLink="/signup" routerLinkActive="active">Signup</a>
    </li>
    <li *ngIf="userIsAuthenticated">
      <button mat-button >Logout</button>
    </li>
  </ul>
</mat-toolbar>
