import { Component } from '@angular/core';
import {FormBuilder, NgForm} from "@angular/forms";
import {AuthService} from "../auth.service";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  isLoading:boolean=false;
  constructor(private authService: AuthService) {
  }
  onLogin(form:NgForm){
    if(form.invalid){
      return;
    }
    this.authService.login(form.value.email, form.value.password);
  }
}
<mat-card>
  <form (submit)="onLogin(loginForm)" #loginForm="ngForm">

    <mat-form-field>
      <input matInput name="email" type="email" placeholder="E-Mail" ngModel email required autocomplete="username"
             #emailInput="ngModel">
      <mat-error *ngIf="emailInput.invalid">E-Mail is a required field</mat-error>
    </mat-form-field>
    <mat-form-field>
      <input matInput name="password" type="password" placeholder="Password" ngModel required autocomplete="current-password"
             #passwordInput="ngModel">
      <mat-error *ngIf="passwordInput.invalid">Password is a required field</mat-error>
    </mat-form-field>
    <button mat-raised-button color="primary" type="submit">Login</button>

  </form>
</mat-card>
