import {Component, OnInit} from '@angular/core';
import {Post} from "../post.model";
import {NgForm} from "@angular/forms";
import {PostService} from "../post.service";

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.css']
})
export class PostCreateComponent implements OnInit{

  constructor(public postService: PostService) {
  }

  ngOnInit() {
  }

  onAddPost(form: NgForm) {
    if (form.invalid) {
      return;
    }
    this.postService.addPost(form.value.title, form.value.content);
    form.resetForm()
  }
}
<!--two way binding-->
<mat-card>

  <form (submit)="onAddPost(postForm)" #postForm="ngForm">

    <mat-form-field>

      <input matInput type="text" name="title" placeholder="Enter Title" ngModel required minlength="3"
             #title="ngModel">
      <mat-error *ngIf="title.invalid">Title is a required field</mat-error>

    </mat-form-field>
    <mat-form-field>

      <textarea matInput rows="4" name="content" placeholder="Enter Content" ngModel required minlength="5"
                #content="ngModel"></textarea>

      <mat-error *ngIf="content.invalid">Content is a required field</mat-error>
    </mat-form-field>

    <button mat-raised-button color="primary" type="submit">Submit</button>

  </form>

</mat-card>
